// This file will contain your character data
export const characters = [
  { name: 'Cable', ratio: 5 },
  { name: 'Iron Man', ratio: 5 },
  { name: 'Magneto', ratio: 5 },
  { name: 'Sentinel', ratio: 5 },
  { name: 'Storm', ratio: 5 },
  { name: 'Doctor Doom', ratio: 5 },
  // ... Add all other characters
  { name: 'Roll', ratio: -1 },
  { name: 'Servbot', ratio: -1 },
];

export const getRandomCharacter = (excludeList = []) => {
  const availableCharacters = characters.filter(char => !excludeList.includes(char.name));
  return availableCharacters[Math.floor(Math.random() * availableCharacters.length)];
};

export const getCharactersByRatio = (ratio) => {
  return characters.filter(char => char.ratio === ratio);
};
