import React, { useState, useEffect } from 'react';
import { characters, getRandomCharacter } from '../data/characters';

const TeamGenerator = () => {
  const [team, setTeam] = useState([]);
  const [isSpinning, setIsSpinning] = useState(false);

  const generateTeam = () => {
    setIsSpinning(true);
    setTimeout(() => {
      const newTeam = [];
      for (let i = 0; i < 3; i++) {
        const char = getRandomCharacter(newTeam.map(c => c.name));
        newTeam.push(char);
      }
      setTeam(newTeam);
      setIsSpinning(false);
    }, 2000); // Stop after 2 seconds
  };

  const renderCharacterSlot = (index) => {
    const char = team[index];
    if (isSpinning) {
      return (
        <div key={`slot-${index}`} className="character-card spinning">
          <img src={characters[Math.floor(Math.random() * characters.length)].image} alt="Spinning" />
        </div>
      );
    } else if (char) {
      return (
        <div key={char.name} className="character-card">
          <img src={char.image} alt={char.name} onError={(e) => { e.target.onerror = null; e.target.src = '/images/characters/default.png' }} />
          <p>{char.name}</p>
        </div>
      );
    } else {
      return (
        <div key={`slot-${index + 1}`} className="character-card">
          <img src={`/images/characters/slot-${index + 1}.png`} alt={`Empty Slot ${index + 1}`} />
          <p>Empty Slot</p>
        </div>
      );
    }
  };

  useEffect(() => {
    if (isSpinning) {
      const interval = setInterval(() => {
        setTeam([...Array(3)].map(() => characters[Math.floor(Math.random() * characters.length)]));
      }, 50); // Increased speed: changed from 100ms to 50ms
      return () => clearInterval(interval);
    }
  }, [isSpinning]);

  return (
    <div>
      <h2>Random Team Generator</h2>
      <div className="team-display">
        {[0, 1, 2].map(renderCharacterSlot)}
      </div>
      <button onClick={generateTeam} disabled={isSpinning}>
        {isSpinning ? 'Generating...' : 'Generate Team'}
      </button>
    </div>
  );
};

export default TeamGenerator;
