import React, { useState } from 'react';
import TeamGenerator from './components/TeamGenerator';
import RatioTeamBuilder from './components/RatioTeamBuilder';
import { characters } from './data/characters';

function App() {
  const [mode, setMode] = useState('regular');

  const charactersByRatio = characters.reduce((acc, char) => {
    if (!acc[char.ratio]) acc[char.ratio] = [];
    acc[char.ratio].push(char);
    return acc;
  }, {});

  return (
    <div className="App">
      <div className="menu">
        <h1>Marvel vs Capcom 2 Team Generator</h1>
        <div>
          <button onClick={() => setMode('regular')}>Regular</button>
          <button onClick={() => setMode('ratio')}>Ratio (7 Points)</button>
        </div>
        {mode === 'regular' ? <TeamGenerator /> : <RatioTeamBuilder />}
      </div>
      <div className="character-select">
        {[5, 4, 3, 2, 1, 0, -1].map(ratio => (
          <div key={ratio} className="character-row">
            <h3>{ratio} Point Characters</h3>
            <div className="character-list">
              {charactersByRatio[ratio].map(char => (
                <div key={char.name} className="character-avatar">
                  <img src={char.image} alt={char.name} />
                  <span className="character-name">{char.name}</span>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
