import React, { useState } from 'react';
import { getRandomCharacter } from '../data/characters';

const TeamGenerator = () => {
  const [team, setTeam] = useState([null, null, null]);

  const generateTeam = () => {
    const newTeam = [];
    for (let i = 0; i < 3; i++) {
      const char = getRandomCharacter(newTeam.map(c => c.name));
      newTeam.push(char);
    }
    setTeam(newTeam);
  };

  return (
    <div>
      <h2>Random Team Generator</h2>
      <div className="team-display">
        {team.map((char, index) => (
          <div key={index} className="character-card">
            {char ? (
              <>
                <img src={char.image} alt={char.name} onError={(e) => { e.target.onerror = null; e.target.src = '/images/characters/default.png' }} />
                <p>{char.name}</p>
              </>
            ) : (
              <>
                <img src={`/images/characters/slot-${index + 1}.png`} alt={`Empty Slot ${index + 1}`} />
                <p>Empty Slot</p>
              </>
            )}
          </div>
        ))}
      </div>
      <button onClick={generateTeam} className="primary-button">
        Generate Team
      </button>
    </div>
  );
};

export default TeamGenerator;
