import React, { useState } from 'react';
import { getRandomCharacter } from '../data/characters';

const TeamGenerator = () => {
  const [team, setTeam] = useState([]);
  const [assists, setAssists] = useState([]);

  const generateTeam = () => {
    const newTeam = [];
    for (let i = 0; i < 3; i++) {
      const char = getRandomCharacter(newTeam.map(c => c.name));
      newTeam.push(char);
    }
    setTeam(newTeam);
    setAssists([]);
  };

  const generateAssists = () => {
    const newAssists = team.map(() => Math.floor(Math.random() * 3) + 1);
    setAssists(newAssists);
  };

  const addCharacter = (character) => {
    if (team.length < 3) {
      setTeam([...team, character]);
    }
  };

  return (
    <div>
      <h2>Team Generator</h2>
      <button onClick={generateTeam}>Generate Random Team</button>
      <button onClick={generateAssists} disabled={team.length === 0}>Generate Assists</button>
      <div className="selected-team">
        {[0, 1, 2].map((index) => (
          <div key={index} className="team-slot">
            {team[index] ? (
              <img src={team[index].avatar} alt={team[index].name} />
            ) : (
              '?'
            )}
            {assists[index] && <span>Assist {assists[index]}</span>}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TeamGenerator;
