import React, { useState } from 'react';
import TeamGenerator from './components/TeamGenerator';
import RatioTeamBuilder from './components/RatioTeamBuilder';
import { characters } from './data/characters';

function App() {
  const [mode, setMode] = useState('regular');
  const [regularTeam, setRegularTeam] = useState([]);
  const [ratioTeam, setRatioTeam] = useState([]);
  const [remainingPoints, setRemainingPoints] = useState(7);

  const handleCharacterClick = (char) => {
    if (mode === 'regular') {
      if (regularTeam.length < 3) {
        setRegularTeam([...regularTeam, char]);
      }
    } else if (mode === 'ratio') {
      if (ratioTeam.length < 3 && remainingPoints >= char.ratio) {
        setRatioTeam([...ratioTeam, char]);
        setRemainingPoints(remainingPoints - char.ratio);
      }
    }
  };

  return (
    <div className="App">
      <div className="menu">
        <h1>MVC2 Team Generator</h1>
        <button onClick={() => setMode('regular')}>Regular</button>
        <button onClick={() => setMode('ratio')}>Ratio (7 Points)</button>
        {mode === 'regular' ? (
          <TeamGenerator team={regularTeam} setTeam={setRegularTeam} />
        ) : (
          <RatioTeamBuilder
            team={ratioTeam}
            setTeam={setRatioTeam}
            remainingPoints={remainingPoints}
            setRemainingPoints={setRemainingPoints}
          />
        )}
      </div>
      <div className="character-select">
        {characters.map((char) => (
          <div
            key={char.name}
            className={`character-avatar ${
              (mode === 'regular' && regularTeam.length === 3) ||
              (mode === 'ratio' && (ratioTeam.length === 3 || remainingPoints < char.ratio))
                ? 'disabled'
                : ''
            }`}
            style={{
              backgroundImage: `url(${char.avatar})`,
              gridRow: char.position.row,
              gridColumn: char.position.col
            }}
            onClick={() => handleCharacterClick(char)}
          >
            <span className="character-name">{char.name}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
